mapper.select:
select *
from member
where user_id = #{id} and user_password=#{password}

select * from member


		and user_id=#{id}


		and user_email = #{eamil}


		and user_name like '%#{name}%'

select * from member


		and user_id=#{id}


		and user_email = #{eamil}


		and user_name like '%${name}%'

mapper.update:
update member
user_password = #{password}, user_email = #{email}, user_name = #{name}, user_birth = #{birth}, user_phone = #{phone}, user_profile_picture = #{profilePicture},
where user_id = #{id}

update member
set token = #{token}
where user_id = #{id}

mapper.select:
select token
from member
where user_id = #{id}

mapper.update:
update member
set token = #{token, jdbcType=VARCHAR}
where user_id = #{id}

update friend set status = 1
where user_id = #{userId} 
and friend_id = #{friendId}
